import Place from "./components/Place";
import * as getTypePlayer from "./App";
import "./GridBord.css";

const sizeBord = 9;
const template = document.createElement("template");
template.innerHTML = `
<div class="body">
  <p><slot name = "place"/> </p>

</div>
`;
class BordGrid extends HTMLElement {
  constructor() {
    super();
    for (let i = 1; i <= sizeBord; i++) {
      this.attachShadow({ mode: "open" });
      this.shadowRoot.appendChild(template.content.cloneNode(true));
    }
  }
  // connectedCallback() {
  //   let typePlayer = getTypePlayer.typePlayer;
  //     this.innerHTML = `<Place name=${i} player=${typePlayer} /> `;
  //   }
  // }
}
window.customElements.define("bord-template", BordGrid);

export default BordGrid;
