import "./CoinFlip.css";
import React from "react";
import { useState } from "react";

function coinToss() {
  this.setState({ nader: "" }, () => {
    if (Math.random() < 0.5) {
      this.setState({ result: "heads" });
      console.log("heads");
    } else {
      this.setState({ result: "tails" });
      console.log("tails");
    }
  });
}

function CoinFlip(props) {
  this.state = {
    result: " ",
    nader: "nader",
  };
  this.coinToss = this.coinToss.bind(this);

  return (
    <div
      id="coin"
      onClick={coinToss}
      className={this.state.result}
      key={+new Date()}
    >
      <div className="side-a">{/* <h1>X</h1> */}</div>
      <div className="side-b">{/* <h1>O</h1> */}</div>
    </div>
  );
}

export default CoinFlip;
