import "./Scores.css";

const template = document.createElement("template");
template.innerHTML = `
<style>
.body {
  font-family: "Arial", sans-serif;
  font-size: large;
  font-weight: bold;
  display: grid;
  grid-template-columns: repeat(3, 200px);
  grid-template-rows: repeat(2, 30px);
  column-gap: 5px;
  row-gap: 5px;
  color: white;
}
</style>
  <div class="body">

    <p> Player X </p>
    <p> TIE </p>
    <p> Player O </p>

    <h3><slot name="px" /></h3>
    <h4><slot name="tie" /></h4>
    <h5><slot name="po" /></h5>

</div>
`;

class Scores extends HTMLElement {
  constructor() {
    super();

    this.showInfo = true;
    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
    this.shadowRoot.querySelector("h3").innerText = this.getAttribute("value");
  }
  get value() {
    return this.getAttribute("value");
  }
  attributeChangedCallback(value) {
    console.log("hear!");
    this.shadowRoot.querySelector("h3").innerText.displayVal.innerText =
      this.getAttribute("px");
    this.shadowRoot.querySelector("h3").innerText.displayVal.innerText =
      this.setAttribute("po");
    this.shadowRoot.querySelector("h3").innerText.displayVal.innerText =
      this.setAttribute("tie");
  }
}
window.customElements.define("user-scores", Scores);

export default Scores;
