import "./Save.css";
import * as getPlace from "./Place";
const template = document.createElement("template");
template.innerHTML = `
<style>

</style>
  <div class="body-save">
  <button id="save-info">save</button>
  </div>
`;

const bord = getPlace.bord;

class Save extends HTMLElement {
  constructor() {
    super();

    this.showInfo = true;
    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
  }

  toggleInfo() {
    this.showInfo = !this.showInfo;

    const info = this.shadowRoot.querySelector(".info");
    const toggleBtn = this.shadowRoot.querySelector("#toggle-info");

    if (this.showInfo) {
      info.style.display = "block";
      toggleBtn.innerText = "Hide Info";
    } else {
      info.style.display = "none";
      toggleBtn.innerText = "Show Info";
    }
  }

  connectedCallback() {
    function handleClick() {
      // localStorage.setItem("bord", JSON.stringify(bord));
      console.log("save bord");
    }

    <div class="body-save">
      <button onClick={handleClick}>save</button>
    </div>;
  }
}
window.customElements.define("save-game", Save);

export default Save;
