function IsWin(props) {
  var win = true,
    player,
    bord = props.bord;

  for (let i = 0; i < 3; i++) {
    player = bord[i][0];
    for (let j = 0; j < 3; j++) {
      if (bord[i][j] !== player) win = false;
    }
    if (player !== "." && win) {
      console.log("win" + player);
      return player;
    }
  }
  for (let i = 0; i < 3; i++) {
    player = bord[0][i];
    for (let j = 0; j < 3; j++) {
      if (bord[i][j] !== player) win = false;
    }
    if (player !== "." && win) {
      console.log("win" + player);
      return player;
    }
  }

  if (((bord[0][0] === bord[1][1]) === bord[2][2]) !== ".") {
    console.log("win" + bord[0][0]);
    return bord[0][0];
  }

  if (((bord[0][2] === bord[1][1]) === bord[2][0]) !== ".") {
    console.log("win" + bord[0][2]);
    return bord[0][2];
  }

  return "lost";
}

