import "./CoinFlip.css";
let player;

function coinToss() {
  // <script>
  jQuery(document).ready(function ($) {
    $("#coin").on("click", function () {
      var flipResult = Math.random();
      $("#coin").removeClass();
      setTimeout(function () {
        if (flipResult <= 0.5) {
          $("#coin").addClass("heads");
          player = "X";
          console.log("it is head ~ " + player);
        } else {
          $("#coin").addClass("tails");
          player = "O";
          console.log("it is tails ~ " + player);
        }
      }, 100);
    });
  });
  // </script>;
}

function CoinFlip(props) {
  return (
    <div>
      <h2>Click on coin to flip</h2>
      <div id="coin">
        <div className="side-a">
          <h1>X</h1>
        </div>
        <div className="side-b">
          <h1>O</h1>
        </div>
      </div>
      <button onClick={coinToss}>Toss the coin</button>
    </div>
  );
}

export default CoinFlip;
