import "./CoinFlip.css";
import * as geTypePlayer from "./App";

let player = geTypePlayer.typePlayer;

function coinToss() {
  // <script>
  jQuery(document).ready(function ($) {
    var spinArray = [
      "animation900",
      "animation1080",
      "animation1260",
      "animation1440",
      "animation1620",
      "animation1800",
      "animation1980",
      "animation2160",
    ];

    function getSpin() {
      var spin = spinArray[Math.floor(Math.random() * spinArray.length)];
      return spin;
    }

    $("#coin").on("click", function () {
      $("#coin").removeClass();

      setTimeout(function () {
        $("#coin").addClass(getSpin());
      }, 100);
    });
  });
  // </script>;
}

function CoinFlip(props) {
  return (
    <div>
      <h2>Click on coin to flip</h2>
      <div id="coin">
        <div className="side-a"></div>
        <div className="side-b"></div>
      </div>
      <button onClick={coinToss}>Toss the coin</button>
    </div>
  );
}

export default CoinFlip;
