  "T",
];
var lastnumClicked = getGrid.numClicked || 0;

const Save = () => {
  // const [bord, setBord] = useState(() => {
  //   // getting stored value
  //   const saved = localStorage.getItem("bord");
  //   const initialValue = JSON.parse(saved);
  //   return initialValue || bord;
  // });
  const [numClicked, setNumClicked] = useState(() => {
    // getting stored value
    const saved = localStorage.getItem("numClicked");
    const initialValue = JSON.parse(saved);
    return initialValue || numClicked;
  });

  <input type="submit" value="save"></input>;
  useEffect(() => {
    // storing input name
    localStorage.setItem("numClicked", JSON.stringify(numClicked));
  }, [numClicked]);

  return (
    <form>
      <input className="bntSubmit" type="submit" value="save"></input>
    </form>
  );
};

export default Save;
