import $ from "jquery";
import "./CoinFlip.css";
import * as geTypePlayer from "./App";

let player = geTypePlayer.typePlayer;

function coinToss() {
  // <script>
  {
    /* jQuery(document).ready(function ($)
    {$("#coin").on("click", function () { */
  }
  var flipResult = Math.random();
  document.querySelector("#coin").removeClass();
  // $("#coin").removeClass();
  setTimeout(function () {
    if (flipResult <= 0.5) {
      $("#coin").addClass("heads");
      // document.querySelector("#coin").add
      player = "X";
      console.log("it is head ~ " + player);
    } else {
      $("#coin").addClass("tails");
      player = "O";
      console.log("it is tails ~ " + player);
    }
  }, 100);
  //   })}
  //   );
  // </script>;
}

class CoinFlip extends HTMLElement() {
  constructor() {
    super();
    this.innerHTML = `
   <div>
   <h2>Click on coin to flip</h2>
   <div id="coin">
     <div className="side-a"></div>
     <div className="side-b"></div>
   </div>

 </div>`;
  }
}
window.customElements.define("coin-flip", CoinFlip);

export default CoinFlip;
