import "./CoinFlip.css";

function coinToss() {
  jQuery(document).ready(function ($) {
    $("#coin").on("click", function () {
      var flipResult = Math.random();
      $("#coin").removeClass();
      setTimeout(function () {
        if (flipResult <= 0.5) {
          $("#coin").addClass("heads");
          console.log("it is head");
        } else {
          $("#coin").addClass("tails");
          console.log("it is tails");
        }
      }, 100);
    });
  });
}

function CoinFlip(props) {
  return (
    <div
      id="coin"
      onClick={coinToss}
      className={this.state.result}
      key={+new Date()}
    >
      <div className="side-a">{/* <h1>X</h1> */}</div>
      <div className="side-b">{/* <h1>O</h1> */}</div>
    </div>
  );
}

export default CoinFlip;
