import "./CoinFlip.css";
import { setState } from "./react";
function coinToss() {
  this.setState({ nader: "" }, () => {
    if (Math.random() < 0.5) {
      this.setState({ result: "heads" });
      console.log("heads");
    } else {
      this.setState({ result: "tails" });
      console.log("tails");
    }
  });
}
function CoinFlip(props) {
  return (
    <div id="coin" onClick={coinToss}>
      <div className="side-a">
        <h1>X</h1>
      </div>
      <div className="side-b">
        <h1>O</h1>
      </div>
    </div>
  );
}

export default CoinFlip;
