import "./Place.css";

var numClicked = 0;
var initBord = localStorage.getItem("bord");
initBord = JSON.parse(initBord);
const sizeBord = 3;

var emptyBord = [];
for (let i = 0; i < Math.pow(sizeBord, 2); i++) emptyBord[i] = " ";
emptyBord[sizeBord] = "T";
console.log(emptyBord);
const bord = initBord ?? emptyBord;

function Place(props) {
  function handleClick() {
    if (bord[props.name] === " ") {
      bord[props.name] = props.player;
      if (bord[sizeBord] === "F") {
        numClicked = 0;
        bord[sizeBord] = "T";
      }
      numClicked++;
    }
  }

  return (
    <button className="place-button" onClick={handleClick}>
      {bord[props.name]}
    </button>
  );
}
export { bord, numClicked, sizeBord };
export default Place;
