import "./Place.css";

var numClicked = 0;
const x=<p className = "x">X</p>;
const o=<p className = "o">O</p>;
const bord = [" ", " ", " ", " ", " ", " ", " ", " ", " ", "T"];

function Place(props) {
  function handleClick() {
    if (bord[props.name] === " ") {
      bord[props.name] = props.player;
      if (bord[9] === "F") {
        numClicked = 0;
        bord[9] = "T";
      }
      numClicked++;
    }
    console.log(numClicked);
  }

  return (
    <button className="place-button" onClick={handleClick}>
      {bord[props.name]}
    </button>
    //same HTML code
  );
}
export { bord, numClicked };
export default Place;
