import { useState, useEffect } from "react";

const Save = () => {
  const [name, setName] = useState("");

  <input type="submit" value="Submit"></input>;
  useEffect(() => {
    // storing input name
    localStorage.setItem("name", JSON.stringify(name));
  }, [name]);

  return (
    <form>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Full name"
        aria-label="fullname"
      />
    </form>
  );
};

export default Save;
