import Place from "./components/Place";
import * as getBord from "./components/Place";

const template = document.createElement("template");
template.innerHTML = htmlInner;

const bord = getBord.bord;
var gridplaces;
var htmlInner;

class GridGame extends HTMLElement {
  constructor() {
    super();

    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
  }
  connectedCallback() {
    var typePlayer = "X";
    let i = 0;
    var htmlInner = `<div> aaa </div>`;
    gridplaces = bord.map(() => {
      htmlInner = htmlInner + `<Place name=${i} player=${typePlayer} />`;
      i++;
    });
  }

  // set content(val) {
  //   // this._content = val;
  //   // // render changes
  //   // let el = this.shadowRoot.querySelector("#content");
  //   // el.textContent = JSON.stringify(this._content, 0, 2);
  //   //   var gridplaces;
  //   //   for (let i = 0; i < 9; i++) {
  //   //     gridplaces[i] = <Place name={i} player={typePlayer} />;
  //   //   }
  // }
  get content() {
    return gridplaces;
  }

  // disconnectedCallback() {
  //   this.shadowRoot.querySelector("#grid-bord").removeEventListener();
  // }
}
window.customElements.define("grid-game", GridGame);

export default GridGame;
