import "./OnePlace.css";
import * as getPlayer from "./App";

const template = document.createElement("template");
template.innerHTML = `
<style>
.place-body button {
    color: white;
    font-size: xx-large;
    width: 100px;
    height: 100px;
    background: black;
    border: none;
    font-weight: bold;
  }

  .place-button:hover {
    background-color: rgba(3, 3, 32, 0.884);
    font-style: italic;
    color: transparent;
    text-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  }

  .place-button:focus {
    text-shadow: 0 0 5px rgba(0, 0, 0, 0.5);
  }
</style>

`;
var typePlayer;
var numClicked = 0;
var initBord = localStorage.getItem("bord");
initBord = JSON.parse(initBord);
const sizeBord = 9;

var emptyBord = [];
for (let i = 0; i < sizeBord; i++) emptyBord[i] = " ";
emptyBord[sizeBord] = "T";

const bord = initBord ?? emptyBord;
var index;

class PlaceWEB extends HTMLElement {
  constructor() {
    super();
    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
  }

  placebutton() {
    if (bord[index] === " ") {
      console.log("get: " + index);

      bord[index] = this.getAttribute("typePlayer");
      if (bord[sizeBord] === "F") {
        numClicked = 0;
        bord[sizeBord] = "T";
      }
      numClicked++;
    }
  }

  connectedCallback() {
    index = parseInt(this.getAttribute("name"), 10);
    typePlayer = this.getAttribute("typePlayer");
    // console.log("tp: " + typePlayer + ",   index: " + index);
    var p = bord[index];
    template.innerHTML += ` <button id="placeButton" class="place-button">
    ${p}
  </button>`;
    // const button = document.getElementById("placeButton");
    // button.addEventListener("click", () => {
    //   button.textContent = `${typePlayer}`;
    //   console.log("tp: " + typePlayer + ",   index: " + index);
    // });
    // button.onclick=function() {
    //   index =  parseInt(this.getAttribute('name'),10);
    //   console.log("get: " +index);
    //   if (bord[index] === " ") {
    //     bord[index] = typePlayer;
    //     console.log(bord);
    //       if (bord[sizeBord] === "F") {
    //         numClicked = 0;
    //         bord[sizeBord] = "T";
    //       }
    //       numClicked++;
    //     }
    // }

    button.addEventListener("click", () => this.placebutton());
  }

  disconnectedCallback() {
    document.getElementById("place-button").removeEventListener();
    // this.shadowRoot.querySelector("#place-button").removeEventListener();
  }
}
window.customElements.define("place-component", PlaceWEB);

export { bord, numClicked, sizeBord };
export default PlaceWEB;
