import Place from "./components/Place";
import * as getBord from "./components/Place";

const template = document.createElement("template");
template.innerHTML = `
<style>
 .* {
  margin: auto;
  width: 310px;
  height: 310px;
  display: grid;
  grid-template-columns: repeat(3, 100px);
  grid-template-rows: repeat(3, 1fr);
  column-gap: 5px;
  row-gap: 5px;
  align-items: start;
  background-color: white;
}
  }
</style>

`;

class GridGame extends HTMLElement {
  constructor() {
    super();
    for (let i = 0; i < 9; i++) {
      if (i === 0) template.innerHTML += `<div>`;
      template.innerHTML += `<Place name=${i} player="X" />`;
      if (i === 8) template.innerHTML += `</div>`;
    }
    console.log(template.innerHTML);

    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
  }
}
window.customElements.define("grid-game", GridGame);

export default GridGame;
