import "./App.css";
// import ReactDOM from "react-dom";
import { useState } from "react";
import Place from "./Place";
import * as getPlace from "./Place";
import React from "react";
// import Save from "./Save";
import Scores from "./Scores";
// import CountUp from "react-countup";

const bord = getPlace.bord;
var numClicked,
  //isClicked = 1,
  numsWinX = 0,
  numsWinO = 0,
  numsTie = 0;

function App() {
  const [typePlayer, setTypePlayer] = useState("X");
  function handleClick() {
    if (typePlayer === "X") {
      setTypePlayer("O");
    } else {
      setTypePlayer("X");
    }

    numClicked = getPlace.numClicked;
    if (numClicked === 9) {
      resetGame(" TIE ", null);
    } else {
      var retval = isWin([
        [bord[0], bord[1], bord[2]],
        [bord[3], bord[4], bord[5]],
        [bord[6], bord[7], bord[8]],
      ]);
      if (retval === "X" || retval === "O") {
        resetGame("Congratulations " + retval + " won!", retval);
      }
    }
  }

  return (
    <div className="App">
      <div className="heder">
        <div>
          {/* <Save /> */}
          <h1>The Tic Tok Toe game</h1>
          {/* <CountUp end={100} /> */}
        </div>
        <h2>Turn player: {typePlayer}</h2>
        {/* <user-instructions name ="jone Doe">
           <div slot = "instructions">
              <p>1. The game is played on a grid that's 3 squares by 3 squares.</p>
              <p>2. You are X, your friend (or the computer in this case) is O. Players take turns putting their marks in empty squares.</p>
              <p>3. The first player to get 3 of her marks in a row (up, down, across, or diagonally) is the winner.</p>
              <p>4. When all 9 squares are full, the game is over. If no player has 3 marks in a row, the game ends in a tie.</p>
           </div>
         </user-instructions> */}
      </div>
      <div>
        <h3>Player x</h3>
        <h3>TIE</h3>
        <h3>Player O</h3>
      </div>
      <div className="bord" onClick={handleClick}>
        <Place className="b1" name="0" player={typePlayer} />
        <Place className="b2" name="1" player={typePlayer} />
        <Place className="b3" name="2" player={typePlayer} />
        <Place className="b4" name="3" player={typePlayer} />
        <Place className="b5" name="4" player={typePlayer} />
        <Place className="b6" name="5" player={typePlayer} />
        <Place className="b7" name="6" player={typePlayer} />
        <Place className="b8" name="7" player={typePlayer} />
        <Place className="b9" name="8" player={typePlayer} />
      </div>
      <users-scores className="userScores">
        <div slot="px" value={numsWinX}>
          {numsWinX}
        </div>
        <div slot="tie" value={numsTie}>
          {numsTie}
        </div>
        <div slot="po" value={numsWinO}>
          {numsWinO}
        </div>
      </users-scores>
    </div>
  );
}

function isWin(bord1) {
  var win = true,
    player;

  //check if ther is a win in row
  for (let i = 0; i < 3; i++) {
    player = bord1[i][0];
    win = true;
    for (let j = 0; j < 3; j++) {
      if (bord1[i][j] !== player) win = false;
    }
    if (player !== " " && win === true) {
      return player;
    }
  }
  //check if ther is a win in colomn
  for (let i = 0; i < 3; i++) {
    player = bord1[0][i];
    win = true;
    for (let j = 0; j < 3; j++) {
      if (bord1[j][i] !== player) win = false;
    }
    if (player !== " " && win === true) {
      return player;
    }
  }
  //check if ther is a win אלכסון
  player = bord1[1][1];
  if (bord1[0][0] === player && player === bord1[2][2] && player !== " ") {
    return bord1[0][0];
  }

  if (bord1[0][2] === player && player === bord1[2][0] && player !== " ") {
    return bord1[0][2];
  }
  return "nobady";
}

function resetGame(msg, winner) {
  alert(msg);
  numsWinX = winner === "X" ? numsWinX++ : numsWinX;
  numsWinO = winner === "O" ? numsWinO++ : numsWinO;
  numsTie = winner === null ? numsTie : numsTie++;
  console.log("the winner: " + winner);
  for (let i = 0; i < 9; i++) bord[i] = " ";
  bord[9] = "F";
  // isClicked = 0;
}
export default App;
