import Place from "./components/Place";
import * as getTypePlayer from "./App";
import "./GridBord.css";

const sizeBord = 9;
const template = document.createElement("template");
let playerVal;

template.innerHTML = `
<div class="body">
  <Place name="1" player=${playerVal} />
</div>
`;
class BordGrid extends HTMLElement {
  constructor() {
    super();
    // this.attachShadow({ mode: "open" });
    // this.shadowRoot.appendChild(template.content.cloneNode(true));
  }
  connectedCallback() {
    playerVal = this.attributes.name.value;
    console.log("playerVal " + playerVal);
    // for (var i = 1; i <= sizeBord; i++)
    this.innerHTML = ` 
    <div><Place name="1" player=${playerVal} /> </div>
    <div> <Place name="2" player=${playerVal} /></div>
    <div><Place name="3" player=${playerVal} /></div>
    <div><Place name="4" player=${playerVal} /></div>
    <div><Place name="5" player=${playerVal} /></div>
    <div><Place name="6" player=${playerVal} /></div>
    <div><Place name="7" player=${playerVal} /></div>
    <div><Place name="8" player=${playerVal} /> </div>`;
  }
}
window.customElements.define("bord-template", BordGrid);

export default BordGrid;
