import "./Save.css";
import * as getPlace from "./Place";

const template = document.createElement("template");
template.innerHTML = `
<style>
.body-save button{
    cursor: pointer;
		color: #fff;
		border: 0;
		border-radius: 5px;
		padding: 5px 10px;
  }
</style>
  <div class="body-save">
  <button id="save-bord"><slot name="saveGame" /></button>

  </div>
`;

const bord = getPlace.bord;
let buttonVal;
class Save extends HTMLElement {
  constructor() {
    super();

    this.showInfo = true;
    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
  }

  saveBord() {
    if (buttonVal === "ResetGame")
      localStorage.setItem("bord", JSON.stringify(bord));
    // else if (buttonVal === "ResetGame") {
    //   localStorage.getItem("bord");
    //   bord = JSON.parse(bord);
    // }
  }

  connectedCallback() {
    buttonVal = this.attributes.name.value;
    this.shadowRoot
      .querySelector("#save-bord")
      .addEventListener("click", () => this.saveBord());
  }
}
window.customElements.define("save-game", Save);

export default Save;
