const Save = () => {
  const [name, setName] = useState("");

  return (
    <form>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Full name"
        aria-label="fullname"
      />
      <input type="submit" value="Submit"></input>
      useEffect(() =>{" "}
      {
        // storing input name
        localStorage.setItem("name", JSON.stringify(name))
      }
      , [name]);
    </form>
  );
};

export default Save;
