var typePlayer = "X";

function handleClick() {
  if (typePlayer === "X") {
    console.log("clicked! X");
    typePlayer = "O";
  } else {
    console.log("clicked! O");
    typePlayer = "X";
  }
}

function Place(props) {
  console.log(props.name);
  return <p>{props.name}</p>;
}
const element = <Place name={typePlayer} />;
//ReactDOM.render(element, document.getElementById("root"));

function App() {
  const id = [1, 2, 3, 4, 5, 6, 7, 8, 9];
  const content = id.map((id) => <li> {id} </li>);
  return (
    <div className="App">
      <div className="heder">
        <h1>The Tic Tok toe game</h1>
      </div>
      <div className="bord" onClick={handleClick}>
        <button className="content content1">{element}</button>
        <button className="content content2">1</button>
        <button className="content content3">1</button>
        <button className="content content4">1</button>
        <button className="content content5">1</button>
        <button className="content content6">1</button>
        <button className="content content7">1</button>
        <button className="content content8">1</button>
        <button className="content content9">1</button>
      </div>
    </div>
  );
}

export default App;
