import "./Save.css";
import * as getPlace from "./Place";

const template = document.createElement("template");
template.innerHTML = `
<style>
 .body-save button{
//     cursor: pointer;
// 		color: #fff;
 		border: 20px;
     border-color: white;
//     background-color: black;
     border-radius: 5px;
 		padding: 5px 5px;
  }
</style>
  <div class="body-save">
  <button id="save-bord"><slot name="saveGame" /></button>
  </div>
  <div class="body-reset">
  <button id="reset-bord"><slot name="resetGame" /></button>
  <div>

`;

const bord = getPlace.bord;
let buttonVal;
class Save extends HTMLElement {
  constructor() {
    super();

    this.showInfo = true;
    this.attachShadow({ mode: "open" });
    this.shadowRoot.appendChild(template.content.cloneNode(true));
  }

  saveBord() {
    console.log(buttonVal);
    if (buttonVal === "save")
      localStorage.setItem("bord", JSON.stringify(bord));
    else {
      localStorage.setItem(
        "bord",
        JSON.stringify([" ", " ", " ", " ", " ", " ", " ", " ", " ", "T"])
      );
    }
  }

  connectedCallback() {
    buttonVal = this.attributes.name.value;
    if (buttonVal === "save") {
      const bntreset = this.shadowRoot.querySelector(".body-reset");
      bntreset.style.display = "none";
    }
    if (buttonVal === "reset") {
      const bntsave = this.shadowRoot.querySelector(".body-save");
      bntsave.style.display = "none";
    }
    this.shadowRoot
      .querySelector("#save-bord")
      .addEventListener("click", () => this.saveBord());
  }
  disconnectedCallback() {
    this.shadowRoot.querySelector("#save-bord").removeEventListener();
  }
}
window.customElements.define("save-game", Save);

export default Save;
