import "./App.css";
//import ReactDOM from "react-dom";
import Place from "./Place";
import { useState } from "react";
import * as chooseName from "./Place";

const bord = chooseName.bord;
var numClicked = 0;

function App() {
  // const [player, setPlayer] = useState('.');
  const [typePlayer, setTypePlayer] = useState("X");
  function handleClick() {
    if (typePlayer === "X") {
      setTypePlayer("O");
    } else {
      setTypePlayer("X");
    }

    numClicked++;
    if (numClicked === 9) alert("TIE ");
    else {
      var retval = isWin([
        [bord[0], bord[1], bord[2]],
        [bord[3], bord[4], bord[5]],
        [bord[6], bord[7], bord[8]],
      ]);
      if (retval === "X" || retval === "O") {
        alert("Congratulations " + retval + " won!");
        resetGame();
      }
    }
  }

  return (
    <div className="App">
      <div className="heder">
        <h1>The Tic Tok Toe game</h1>
      </div>
      <h2>Turn player: {typePlayer}</h2>

      <div className="bord" onClick={handleClick}>
        <Place name="0" player={typePlayer} />
        <Place name="1" player={typePlayer} />
        <Place name="2" player={typePlayer} />
        <Place name="3" player={typePlayer} />
        <Place name="4" player={typePlayer} />
        <Place name="5" player={typePlayer} />
        <Place name="6" player={typePlayer} />
        <Place name="7" player={typePlayer} />
        <Place name="8" player={typePlayer} />
      </div>
    </div>
  );
}

function isWin(bord1) {
  console.log("bord:  " + bord1);
  var win = true,
    player;

  for (let i = 0; i < 3; i++) {
    player = bord1[i][0];
    for (let j = 0; j < 3; j++) {
      if (bord1[i][j] !== player) win = false;
    }
    if (player !== "." && win) {
      return player;
    }
  }
  for (let i = 0; i < 3; i++) {
    player = bord1[0][i];
    for (let j = 0; j < 3; j++) {
      if (bord1[i][j] !== player) win = false;
    }
    if (player !== "." && win) {
      return player;
    }
  }
  player = bord1[1][1];
  if (bord1[0][0] === player && player === bord1[2][2] && player !== ".") {
    return bord1[0][0];
  }

  if (bord1[0][2] === player && player === bord1[2][0] && player !== ".") {
    return bord1[0][2];
  }
  console.log("~~~");
  return "nobady";
}

function resetGame() {
  for (let i = 0; i < 9; i++) bord[i] = ".";
}
export default App;

